public class whileLoops {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//0-10          while durum true olduðu  sürece devam eder
		int i=0;
		while(i<=10) {	
		System.out.println(i);
		i++;
		}
		//using loop iterate untill we have 10 stdnts in the room
		
		int numStudent=1;
		while(numStudent<=10) {
			System.out.println("student"+numStudent);
			numStudent++;
		}
		//print 11,10,9....1
		 int num =11;
		 while(num >=1) {
			 System.out.print(num+" ");
			 num--;
		 }
		
		
		 
		 
		
	}

}
